long long int ans;
void Recurrsive2(int nowO, int nowR)
{
    int i;
    if(nowO == 1){
        ans += nowR+1;
        return;
    }
    if(nowO == nowR){
        ans+=2;
        return;
    }
    else if(nowO == nowR-1 ){
        ans += (nowO-1)*2 + 3;
        return;
    }

    for(i = 1; i <= nowR - (nowO-1); i++) Recurrsive2(nowO-1,nowR-i);
}
